function onInit(){myButton.addEventListener("click",myButtonClicked);document.getElementById("clear-count-button").addEventListener("click",clearCount);window.addEventListener("beforeunload",function(){appInsights.trackMetric("User left",clickCount)})}function myButtonClicked(){clickCount++;console.log("Click count: "+clickCount);myButton.innerHTML="You clicked me "+clickCount+" times!"}function clearCount(){clickCount!==0&&(clickCount=0,console.log("Click count reset"),myButton.innerHTML="Click me",appInsights.trackEvent("User Reset Count"))}var clickCount=0,myButton=document.getElementById("my-button");onInit();