function myButtonClicked(){clickCount++;console.log("Click count: "+clickCount);myButton.innerHTML="You clicked me "+clickCount+" times!"}function clearCount(){clickCount!==0&&(clickCount=0,console.log("Click count reset"),myButton.innerHTML="Click me")}var clickCount=0,myButton=document.getElementById("my-button");myButton.addEventListener("click",myButtonClicked);document.getElementById("clear-count-button").addEventListener("click",clearCount);